punctuation_fancy = [
        ('Period', '.'),
        ('Comma', ','),
        ('Colon', ':'),
        ('Semicolon', ';'),
        ('Question_Mark', '?'),
        ('Exclamation_Point', '!'),
        ('Dash', '-'),  # 
        ('Quote', '"'),  # 
        ('Apostrophe', "'"),  # 
        ('Parentheses', '()[]{}'),
        ('Other_Punctuation', '#$%&*+-/<=>@\\^_`|~')
    ]


def clean(text):
    words = [w.strip(punctuation) for w  in text.lower().split()]
    return words

def liwcify(text, normalize = False):
    words = clean(text)
                
    text_properties = {'Word_Count' : len(words),
                       'Big_Words'  :  sum(len(word) > 6 for word in words)}
    
    text_properties['Sentence_Count'] = len(sent_tokenize(text))
    character_counts = Counter(text)
    for name, chars in punctuation_fancy:
        text_properties[name] = sum(character_counts[char] for char in chars)
    text_properties['Parentheses'] = text_properties['Parentheses'] / 2.0
    text_properties['All_Punctutation'] = sum(text_properties[name] for name, _ in punctuation_fancy)

    for word in words:
        #process full words
        if word in liwc_word_categories:
            for category in liwc_word_categories[word]:
                text_properties[category] = text_properties.get(category,0) + 1
        
        #process stems words
        else:
            stems = list(set([word[:i] for i in range(6,15)]))
            for stem in stems:
                if stem in liwc_stem_categories:
                    for category in liwc_stem_categories[stem]:
                        text_properties[category] = text_properties.get(category,0) + 1
    
    if normalize == True:
        for key in text_properties:
            if key not in ['Word_Count','Sentence_Count']+ [p[0] for p in punctuation_fancy]:
                text_properties[key] = text_properties[key] / float(text_properties['Word_Count'])
                
                
        
    return text_properties                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             


